        // 유튜브 댓글은 한줄로 안나와서 한줄로 만들어줘야함
        const tempArr = youtubeText.split(/\r?\n\r?\n/);
        // 유튜브 댓글 아이디와 채팅 사이에 \n이 들어가서 :로 바꿔주기
        const arrYoutube = tempArr.map((data) => data.replace('\n', ': '))

        const tempResult = [...arrTwitch, ...arrYoutube]


        console.log(tempResult)

