import React, { useState, useRef } from 'react'
import logo_twitch from '../Images/twitch_mini_logo.png'
import logo_youtube from '../Images/youtube_mini_logo.png'
import '../Style/style.css'

var a = `
민트_초ㅋ (dmsnwl94): 아하
middleage_: 혹시 최하급보석 많으신분~
햝햝햝햝 (forke1212): 횃불 인벤에 넣는가 안넣는가 보고있습니다 후후
민트_초ㅋ (dmsnwl94): 어쩐지 이야기 하는게 맛갈나게 잘 푸시더라고
불멸의거너스 (kikwsi): 웹소설 작가가 목표이신가요?
불멸의거너스 (kikwsi): 아하..ㅎㅎ 하시면 챙겨볼게요!
gun6750: 저요!
불멸의거너스 (kikwsi): 저 어제 아트마요~
middleage_: 저요~
쉬리한마리 (wnssl611): ㄱ
`
var b = `

foofoo woo
​와 탈갑이네 먹으려고 2주동안 다녔는데 안나오네요

glory kim
​엘드리치 나눔좀.

박세연
​주시면 감사하겠습니당 ^^;;

foofoo woo
​탈갑이나 탈목 드랍으로 드신분 계시나요 혹시 ?

티님t
​안녕하세요~~ 늦었네요~

박세연
​저요!

glory kim
​저요~~
`

const removeOverlab = (arr) => {
    const arrResult = arr.reduce(function(result, data) {
        if (data !== '') 
            result.push(data);
        return result;
      }, []);

    return arrResult;
}
const parsingDatas = (arr, platform) => {
    const arrResult = arr.map((data) => {
        const json ={
            platform,
            id: data.substring(0, data.indexOf(': ')),
            msg: data.substring(data.indexOf(': ') + 2, data.length)
        }
        return json;
    });
    return arrResult;
}

const IdFilter = () => {
    const twitchTextArea = useRef();
    const youtubeTextArea = useRef();

    const [searchText, setSearchText] = useState([]);

    const [chattingText, setChattingText] = useState([]);

    const onChangeSearchText = (e) => {
        const searchText = e.target.value;
        console.log(searchText)
        setSearchText(searchText)
    }

    const onChangeTextArea = (e) => {
        const twitchText = twitchTextArea.current.value;
        const youtubeText = youtubeTextArea.current.value;

        /* Twitch */
        let arrTemp = twitchText.split(/\r?\n/);
        const refinedTwitchData = removeOverlab(arrTemp)
        const arrTwitch = parsingDatas(refinedTwitchData, 'twitch');
        /* // Twitch */

        /* Youtube */
        let tempSplitArr = youtubeText.split(/\r?\n\r?\n/);        // 유튜브 댓글은 한줄로 안나와서 한줄로 만들어줘야함
        tempSplitArr = tempSplitArr.map((data) => data.replace('\n', ': '))      // 유튜브 댓글 아이디와 채팅 사이에 \n이 들어가서 :로 바꿔주기
        const refinedYoutubeData = removeOverlab(tempSplitArr)
        const arrYoutube = parsingDatas(refinedYoutubeData, 'youtube');
        /* // Youtube */

        const arrResult = [...arrTwitch, ...arrYoutube]
 
        setChattingText(arrResult)
    }

    return (
        <div id="id_filter">
            <div className="top_menu">
                <label>검색어:</label>
                <input type='text' onChange={onChangeSearchText} />
                <label>중복 제거</label>
                <input type="checkbox" />
            </div>

            <div className="content">
                <div className="content_box chatting">
                    <div className="twitch_chatting_box">
                        <label>Twitch Chatting</label>
                        <textarea onChange={onChangeTextArea} ref={twitchTextArea} value={a} />
                    </div>

                    <div className="youtube_chatting_box">
                        <label>Youtube Chatting</label>
                        <textarea onChange={onChangeTextArea} ref={youtubeTextArea} value={b} />
                    </div>
                </div>

                <div className="content_box result">
                    <h2>당첨자 {chattingText.length}명</h2>
                    {
                        chattingText.map((data) => {
                            return (
                                <div className="winner_box">
                                    <img src={data.platform === 'twitch' ? logo_twitch : logo_youtube} className={data.platform === 'twitch' ? 'logo_twitch' : 'logo_youtube'} />
                                    <label className="winner_id">{data.id}</label>
                                    <label className="winner_msg">{data.msg}</label>
                                </div>
                            )
                        })
                    }
                </div>

            </div>
        </div>
    );
}

export default IdFilter;
